import React from 'react';
import { Icon, Input} from '../Atom'
import PropTypes from 'prop-types';
import { type } from '@testing-library/user-event/dist/type';

MoleculeInput.protoTypes = {
    icon : PropTypes.string,
    placeholder : PropTypes.string,
    isInvalid: PropTypes.bool,
    isValid: PropTypes.bool,
    className: PropTypes.string,
    onChange: PropTypes.any,
    readOnly: PropTypes.bool,
    value: PropTypes.string,
    bsPrefix: PropTypes.string,
    id: PropTypes.any,
    type: PropTypes.string,
}
 
MoleculeInput.defaultProps ={
    icon : null,
    isInvalid: false,
    isValid: false,
    className: '',
    onChange: null,
    readOnly: false,
    value: null,
    bsPrefix: null,
    id: null,
    placeholder: 'Texte',

}
function MoleculeInput(props) {
    return <>
     <div className='flex item-center pointer'>
    <Input 
      placeholder=   {props.placeholder}
      onChange={(e) => props.changeInput(e,type)}
      type={props.type}
      value={props.state.type.value}
      isValid={props.state.type.isValid}
      isInvalid={props.state.type.isInvalid}
      className='w-100 mb-5' >     
   
  
                
            
    
    </Input>
   
    <Icon
                    icon= 'key'
                    className='small-icon red ml-2'
                />
                  
    </div>
    </>

};

export default MoleculeInput;
