import React from 'react';
import { Form } from 'react-bootstrap';
import PropTypes from 'prop-types';

import {Icon , Input} from '../Atom';


function MoleculeInput(props) {
    const className = ['default-input'];
    
    return (
        <Input
        placeholder={props.placeholder}
        type = {props.type}
        onChange={(e) => props.changeInput(e,'')}
        value={props.state.mail.value}
        isValid={props.state.mail.isValid}
        isInvalid={props.state.mail.isInvalid}
        className='w-100 mb-5'
            
        />

     
      
    );
};

export default MoleculeInput;
