import React from "react";
import { propTypes } from "prop-types";

AtomButton.propTypes = {
    typeButton: PropTypes.string,
    title: PropTypes.any,
    content: PropTypes.any,
    btnTitle: PropTypes.any,
    onClick: PropTypes.any,
    btnType: PropTypes.any,
};
AtomButton.defaultProps = {
    typeButton: null,
    btnTitle: null,
    onClick: null,
};
function AtomButton(props) {
    const typeButton = ['btn btn-light'];
   
    if (props.size === 'meduim') {
        typeButton.push('defaul-meduim-button');
    }
    if (props.size === 'larg') {
        typeButton.push('defaul-larg-button');
    }
    if (props.size === 'small') {
        typeButton.push('defaul-small-button');
    }
    if (props.className) {
        typeButton.push(props.className)
    }
   
};

export default AtomButton;
